import { useState } from "react";

const Home= ()=> {
    const [formData,setFormData] = useState({firstname:"",lastname:"",email:"",car:"",
        city:"",
    })
    function changeHandler(event){
             const {name,value,checked,type} =  event.target;
             setFormData( (prev)=>({
                ...prev,[name]:value }) );
                console.log(formData);
    }
    return(
        <div className="app flex flex-col justify-center items-center">
         <form>
            <label>FirstName:</label>
            <br/>
            <input onChange={changeHandler} type="text" name="firstname" id="firstname" value={formData.firstname} className="outline"/>
            <br/>

            <label>LastName:</label>
            <br/>
            <input type="text" onChange={changeHandler} name="lastname" id="lastname" value={formData.lastname} className="outline"/>
            <br/>

            <label>Email:</label>
            <br/>
            <input type="email" onChange={changeHandler} name="email" id="email" value={formData.email} className="outline"/>
            <br/>
            
            <label>city:</label>
            <input type="text" />
            <br/>
            <label>Car:</label>
            <br/>

            <select onChange={changeHandler} name="car" id="car" value={formData.car} className="outline" >
                <option>Fortuner</option>
                <option>Defender</option>
                <option>Tharr</option>
                <option>Mercedez</option>
            </select>

         </form>
        </div>
    )
}
export default Home;
